import numpy as np

from pysaber.poly import gen_matrix
from pysaber.rng import randombytes
from pysaber.saber_params import SABER_L, SABER_N, SABER_SEEDBYTES
from tests.common import rng_init


def test_gen_matrix():

    rng_init()

    A = np.zeros((SABER_L, SABER_L, SABER_N), dtype=np.int16)
    seed_A = bytearray(SABER_SEEDBYTES)

    randombytes(seed_A, SABER_SEEDBYTES)

    gen_matrix(A, seed_A)

    result = ""
    for i in range(SABER_L):
        for j in range(SABER_L):
            for k in range(SABER_N):
                result += f"{A[i][j][k]:02x}"

    assert (
        result
        == ""
    )
